    public struct Screen
    {
        public const float DEFAULT_DIP = 96f;
        public static readonly Screen OLED_128_64 = new Screen { WidthInPixel = 128, HeightInPixel = 64, DPI = 144f };
        public static readonly Screen OLED_128_32 = new Screen { WidthInPixel = 128, HeightInPixel = 32, DPI = 144f };
        public static readonly Screen OLED_64_32 = new Screen { WidthInPixel = 64, HeightInPixel = 32, DPI = 144f };

        //reference http://microsoft.github.io/Win2D/html/DPI.htm
        public int WidthInPixel { get; private set; }
        public int HeightInPixel { get; private set; }
        public float DPI { get; private set; }
        //DIPs = pixels / (DPI/96.0)
        public float WidthInDIP { get { return WidthInPixel / (DPI / DEFAULT_DIP); } }
        public float HeightInDIP { get { return HeightInPixel / (DPI / DEFAULT_DIP); } }
        public static bool operator ==(Screen a, Screen b)
        {
            return (a.WidthInPixel == b.WidthInPixel && a.HeightInPixel == b.HeightInPixel && a.DPI == b.DPI);
        }
        public static bool operator !=(Screen a, Screen b)
        {
            return !(a == b);
        }
        public override bool Equals(object obj)
        {
            return base.Equals(obj);
        }
        public override int GetHashCode()
        {
            return base.GetHashCode();
        }
    }

